mapper.select:
SELECT c.id AS courseId, c.course_name AS courseName, c.course_nature AS courseNature, col.college_name AS collegeName, maj.major_name AS majorName, dic.dict_name AS courseType, c.total_credits AS totalCredits, c.total_hours AS totalHours, c.total_weeks AS totalWeeks, c.hour_teach AS hourTeach, c.hour_practice AS hourPractice, c.hour_operation AS hourOperation, c.hour_outside AS hourOutside, c.hour_week AS hourWeek, c.required_elective AS requiredElective, c.term AS term, c.remark AS remark FROM course c LEFT JOIN sys_dict dic ON c.dict_id = dic.id LEFT JOIN college col ON c.college_id = col.id LEFT JOIN major maj ON c.major_id = maj.id WHERE c.del_flag = 0
AND c.id = #{id}
AND c.course_nature = #{courseNature}
AND c.course_name LIKE CONCAT('%', #{courseName}, '%')
AND c.college_id = #{collegeId}
AND col.college_name LIKE CONCAT('%', #{collegeName}, '%')
AND c.major_id = #{majorId}
AND maj.major_name LIKE CONCAT('%', #{majorName}, '%')
AND c.course_type = #{dictId}
AND dic.dict_name LIKE CONCAT('%', #{dictName}, '%')
ORDER BY c.create_time DESC
